function findByItem(item: any, path:string):any{
    if(item.type == "menu" && item.path && item.path == path){
        return item;
    }
    if(item.children){
        for(let inItem of item.children){
            if(inItem.type == "btn"){
                continue; // 按钮权限直接跳过
            }
            let findItemObj =  findByItem(inItem, path);
            if(findItemObj){
                return findItemObj;
            }else{
                continue;
            }
        }
    }else{
        return null;
    }
}

/** 根据path寻找菜单项 */
export const findbyPath = function(path:string){
    for(let item of MenuConfig){
        if(item.type == "btn"){
            continue; // 寻找菜单, 按钮直接跳过
        }else if(item.path && item.path == path){
            return item;
        }else{
            let findItem = findByItem(item, path)
            if(findItem){
                return findItem;
            }else{
                continue;
            }
        }
    }
    return null;
}

const findFatherByPathSub = function(willfather:Menu, path:string): Menu | null{
    if(willfather.type == "btn"){
        return null; // 按钮权限组直接返回null
    }else if(willfather.children){
        for(let inItem of willfather.children){
            if(inItem.path && inItem.path == path){
                return willfather;
            }else{
                let findItemObj =  findFatherByPathSub(inItem, path)
                if(findItemObj){
                    return findItemObj
                }else{
                    continue;
                }
            }
        }
    }else{
        return null;
    }
    return null;
}

/** 寻找父级菜单 */
export const findFatherByPath = function(path:string):Menu | null{
    for(let willfather of MenuConfig){
        if(willfather.path && willfather.path == path){
            return null;
        }else{
            let findFatherByPathSubItem = findFatherByPathSub(willfather, path)
            if(findFatherByPathSubItem){
                return findFatherByPathSubItem;
            }else{
                continue;
            }
        }
    }
    return null;
}

function packageTypeMenu(menuList:Menu[], dataList:Menu[]){
    for(let menu of menuList){
        let dataItem:any = {}
        if(menu.type === "menu"){
            dataItem.name = menu.name
            dataItem.path = menu.path
            dataItem.id = menu.id
            dataItem.permission = menu.permission
            dataItem.iconSvg = menu.iconSvg
            dataItem.type = menu.type
            dataItem.showTip = menu.showTip
            dataList.push(dataItem)
        }else if(menu.type === "btn"){
            continue;
        }
        if(menu.children){
            dataItem.children = []
            packageTypeMenu(menu.children, dataItem.children)
            if(dataItem.children.length == 0){
                dataItem.children = null
            }
        }
    }
}

export const findTypeMenu = function(){
    let dataList:Menu[] = []
    packageTypeMenu(MenuConfig, dataList)
    return dataList
}

/** 根据权限寻找菜单项 */
export function findByPermission(menuList:Menu[], permission:string):Menu|null{
    for(let menuItem of menuList){
        if(menuItem.permission === permission){
            return menuItem;
        }
        if(menuItem.children && menuItem.children.length > 0){
            let findSubItem = findByPermission(menuItem.children, permission);
            if(findSubItem != null){
                return findSubItem;
            }
        }
    }
    return null;
}

function findAllChildrenDeepMain(menuList?:Menu[], list?:Menu[]){
    if(!menuList){
        return;
    }
    for(let menuItem of menuList){
        list?.push(menuItem)
        if(menuItem.children && menuItem.children.length > 0){
            findAllChildrenDeepMain(menuItem.children, list)
        }
    }
}

export function findAllChildrenDeep(menuList:Menu[], permission:string):Menu[]{
    let thismenu = findByPermission(menuList, permission)
    if(thismenu == null){
        return [];
    }
    let list:Menu[] = [];
    findAllChildrenDeepMain(thismenu.children, list)
    return list;
}

/** 根据权限寻找默认页面 */
function findDefaultPageMain(menuList?:Menu[], permissions?:string[]|null):Menu|undefined{
    if(menuList == undefined || menuList==null || menuList.length == 0){
        return undefined;
    }
    if(permissions == undefined || permissions==null || permissions.length == 0){
        return undefined;
    }
    for(let menuItem of menuList){
        if(menuItem.path != null && menuItem.path != undefined && menuItem.path.trim() != ""
            && menuItem.type == "menu"
            && permissions.includes(menuItem.permission)){
            return menuItem;
        }
        if(menuItem.children != null && menuItem.children != undefined && menuItem.children.length > 0){
            let menuFindChild = findDefaultPageMain(menuItem.children, permissions)
            if(menuFindChild != undefined && menuFindChild != null){
                return menuFindChild
            }
        }
    }
    return undefined;
}

/** 根据权限寻找默认页面 */
export function findDefaultPage(permissions?:string[]|null):Menu|undefined{
    return findDefaultPageMain(MenuConfig, permissions)
}